var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
	isDateToday: function(strDate){
		//create glidedate objects for current and past dates
		var rightDate = new GlideDateTime();
		var backDate = new GlideDateTime(strDate);
		//get date of both dates
		var today = rightDate.getLocalDate();
		var istoday = backDate.getLocalDate();
		//compare both dates
		if(today.compareTo(istoday)==0){
			return true;
		}
		else{
			return false;
		}
	},
   /* isDatePast: function(strDate) {
